var sitemap = document.getElementsByTagName('li');
for (var i = 0; i < sitemap.length; i++) {
    if (sitemap[i].innerHTML.indexOf("sitemap") > -1) {
        document.getElementsByTagName('li')[i].style.display="none";
    }
}


